import React, { useContext } from 'react';
import { MeuContexto } from '../context/MeuContexto';

const ComponenteConsumidor = () => {
  const { valor, atualizarValor } = useContext(MeuContexto);

  return (
    <div>
      <p>Valor do contexto: {valor}</p>
      <button onClick={() => atualizarValor('Novo valor do contexto')}>
        Atualizar Valor
      </button>
    </div>
  );
};

export default ComponenteConsumidor;
